import React from "react";

const TaskList = ({ tasks, onEditInitiated, onDeleteTask }) => {
  // Function to trigger delete confirmation and operation
  const handleDelete = (taskId) => {
    if (window.confirm("Are you sure you want to delete this task?")) {
      onDeleteTask(taskId);
    }
  };

  return (
    <div>
      <h2>Task List</h2>
      {tasks.length > 0 ? (
        tasks.map((task) => (
          <div key={task._id}>
            <h3>{task.title}</h3>
            <p>{task.description}</p>
            {/* Display other task details as needed */}
            <button onClick={() => onEditInitiated(task)}>Edit</button>
            <button onClick={() => handleDelete(task._id)}>Delete</button>
          </div>
        ))
      ) : (
        <p>No tasks found</p> // Display this message if no tasks are available
      )}
    </div>
  );
};

export default TaskList;
