import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchTasks, deleteTask } from "./redux/tasksSlice";
import TaskList from "./components/TaskList";
import AddTaskForm from "./components/AddTaskForm";
import EditTaskForm from "./components/EditTaskForm";
import "bootstrap/dist/css/bootstrap.min.css"; // Ensure Bootstrap CSS is imported

function App() {
  const dispatch = useDispatch();
  const tasks = useSelector((state) => state.tasks.items);
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    dispatch(fetchTasks());
  }, [dispatch]);

  const handleEditInitiated = (task) => {
    setEditingTask(task);
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const handleTaskUpdated = () => {
    setEditingTask(null);
    dispatch(fetchTasks()); // Re-fetch tasks to update the list
  };

  const handleDeleteTask = (id) => {
    dispatch(deleteTask(id));
  };

  return (
    <div className="container mt-5">
      {" "}
      {/* Bootstrap container for consistent layout and spacing */}
      <h1 className="text-center mb-4">Tasks Manager</h1>
      {editingTask ? (
        <EditTaskForm
          task={editingTask}
          onTaskUpdated={handleTaskUpdated}
          onCancel={handleCancelEdit}
        />
      ) : (
        <>
          <AddTaskForm />
          <TaskList
            tasks={tasks}
            onEditInitiated={handleEditInitiated}
            onDeleteTask={handleDeleteTask}
          />
        </>
      )}
    </div>
  );
}

export default App;
