import React, { useState, useEffect } from "react";
import axios from "axios";

const TaskList = ({ onEditInitiated, onDeleteTask }) => {
  // Accept props from parent component
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/tasks")
      .then((response) => {
        setTasks(response.data);
      })
      .catch((error) =>
        console.log("There was an error fetching the tasks:", error)
      );
  }, []);

  // Function to trigger delete operation
  const handleDelete = (taskId) => {
    if (window.confirm("Are you sure you want to delete this task?")) {
      onDeleteTask(taskId);
    }
  };

  return (
    <div>
      <h2>Task List</h2>
      {tasks.length > 0 ? (
        tasks.map((task) => (
          <div key={task._id}>
            <h3>{task.title}</h3>
            <p>{task.description}</p>
            {/* Optionally render other task properties here */}
            <button onClick={() => onEditInitiated(task)}>Edit</button>
            <button onClick={() => handleDelete(task._id)}>Delete</button>
          </div>
        ))
      ) : (
        <p>No tasks found</p>
      )}
    </div>
  );
};

export default TaskList;
