import React, { useState } from "react";
import axios from "axios";

const EditTaskForm = ({ task, onTaskUpdated, onCancel }) => {
  const [title, setTitle] = useState(task.title);
  const [description, setDescription] = useState(task.description);
  const [dueDate, setDueDate] = useState(task.dueDate.split("T")[0]); // Assuming dueDate is in ISO format
  const [status, setStatus] = useState(task.status);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const updatedTask = { title, description, dueDate, status };

    try {
      await axios.put(
        `http://localhost:5000/api/tasks/${task._id}`,
        updatedTask
      );
      onTaskUpdated({ ...updatedTask, _id: task._id }); // Update locally
    } catch (error) {
      console.error("Failed to update task:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Form fields for title, description, dueDate, and status */}
      {/* ... */}
      <button type="submit">Update Task</button>
      <button type="button" onClick={onCancel}>
        Cancel
      </button>
    </form>
  );
};

export default EditTaskForm;
